import React, { useEffect, useState } from "react";
import "./App.css";

const App: React.FC = () => {
  const [count, setCount] = useState<number>(() => {
    const savedCount = localStorage.getItem("count");
    return savedCount !== null ? parseInt(savedCount, 10) : 0;
  });

  const [countTrue, setCountTrue] = useState<number>(() => {
    const savedCountTrue = localStorage.getItem("countTrue");
    const lastUpdateTime = localStorage.getItem("lastUpdateTime");

    if (
      savedCountTrue !== null &&
      lastUpdateTime !== null &&
      Date.now() - parseInt(lastUpdateTime, 10) < 24 * 60 * 60 * 1000
    ) {
      return parseInt(savedCountTrue, 10);
    } else {
      return 100; // Changed from 100 to 101
    }
  });

  const [canClick, setCanClick] = useState<boolean>(true);

  useEffect(() => {
    localStorage.setItem("count", count.toString());
    localStorage.setItem("countTrue", countTrue.toString());
    localStorage.setItem("lastUpdateTime", Date.now().toString());

    let interval: NodeJS.Timeout;

    if (countTrue === 0) {
      alert("Ð¡ÐºÑ€ÑƒÐ´Ð¶ ÑƒÑÑ‚Ð°Ð» ðŸ˜´");

      interval = setInterval(() => {
        setCountTrue((prevCount) => {
          const newCount = Math.min(prevCount + 1, 1000);
          return newCount;
        });
      }, 30);
    }

    return () => clearInterval(interval);
  }, [countTrue]);

  const handleClick = () => {
    if (canClick) {
      setCount(count + 1);

      if (countTrue > 0) {
        setCountTrue(countTrue - 1);
      }

      setCanClick(false);
      setTimeout(() => {
        setCanClick(true);
      }, 1000);
    }
  };

  return (
    <div className="App">
      <h2 className="title">Quack Coin</h2>
      <p className="count-click">{count}</p>
      <div className="coin-btn">
        <button
          className="coin-button"
          onClick={handleClick}
          disabled={!canClick}
        >
          ...
        </button>
      </div>
      <div className="box-progress">
        <hr className="coin-progress" style={{ width: countTrue * 2 }} />
        <p className="sum-true">
          <b>{countTrue}</b>
        </p>
      </div>
      <div className="nav-bar">
        <button className="nav-bar-btn">...</button>
        <button className="nav-bar-btn">...</button>
        <button className="nav-bar-btn">...</button>
        <button className="nav-bar-btn">...</button>
      </div>
    </div>
  );
};

export default App;
