import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [count, setCount] = useState(() => {
    const savedCount = localStorage.getItem("count");
    return savedCount !== null ? parseInt(savedCount, 10) : 0;
  });

  const [countTrue, setCountTrue] = useState(1000);
  const [canClick, setCanClick] = useState(true);

  useEffect(() => {
    localStorage.setItem("count", count.toString());

    let interval;

    if (countTrue === 0) {
      alert("–°–∫—Ä—É–¥–∂ —É—Å—Ç–∞–ª üò¥");

      interval = setInterval(() => {
        setCountTrue((prevCount) => {
          if (prevCount < 1000) {
            return prevCount + 1;
          } else {
            clearInterval(interval);
            return prevCount;
          }
        });
      }, 5000);
    }

    return () => clearInterval(interval);
  }, [countTrue]); // –ó–∞–≤–∏—Å–∏–º–æ—Å—Ç—å —Ç–æ–ª—å–∫–æ –æ—Ç countTrue

  const handleClick = () => {
    if (canClick) {
      setCount(count + 1);

      if (countTrue > 0) {
        setCountTrue(countTrue - 1);
      }

      setCanClick(false); // –ë–ª–æ–∫–∏—Ä—É–µ–º –∫–ª–∏–∫
      setTimeout(() => {
        setCanClick(true); // –†–∞–∑—Ä–µ—à–∞–µ–º –∫–ª–∏–∫ —á–µ—Ä–µ–∑ 1 —Å–µ–∫—É–Ω–¥—É
      }, 500);
    }
  };

  return (
    <div className="App">
      <h2 className="title">Quack Coin</h2>
      <p className="count-click">{count}</p>
      <div className="coin-btn">
        <button
          className="coin-button"
          onClick={handleClick}
          disabled={canClick}
        >
          ...
        </button>
      </div>
      <div className="box-progress">
        <hr className="coin-progress" style={{ width: countTrue / 10 }} />
        <p>{countTrue}</p>
      </div>
    </div>
  );
}

export default App;
