import React, { useEffect, useState } from "react";
import "./App.css";

const App: React.FC = () => {
  const [count, setCount] = useState<number>(() => {
    const savedCount = localStorage.getItem("count");
    return savedCount !== null ? parseInt(savedCount, 10) : 0;
  });

  const [countTrue, setCountTrue] = useState<number>(100);

  useEffect(() => {
    localStorage.setItem("count", count.toString());

    let interval: NodeJS.Timeout;

    if (countTrue === 0 && count < 10) {
      interval = setInterval(() => {
        if (count < 10) {
          setCount((count) => count + 1);
          setCountTrue((countTrue) => {
            if (countTrue < 100) {
              return countTrue + 10; // Увеличиваем ширину прогресс-бара
            } else {
              clearInterval(interval); // Останавливаем интервал после достижения 100
              return countTrue;
            }
          });
        } else {
          clearInterval(interval);
        }
      }, 1500); // Каждые 1.5 секунды
    }

    return () => clearInterval(interval);
  }, [count, countTrue]);

  const handleClick = () => {
    setCount((count) => count + 1);
    setCountTrue((countTrue) => countTrue - 1);
  };

  return (
    <div className="App">
      <h2 className="title">Quack Coin</h2>
      <p className="count-click">{count}</p>
      <div className="coin-btn">
        <button className="coin-button" onClick={handleClick}>
          ...
        </button>
      </div>
      <div className="coin-progress" style={{ width: countTrue }}>{countTrue}</div>
    </div>
  );
};

export default App;
