import React, { useEffect, useState } from "react";
import "./App.css";

function App() {
  const [count, setCount] = useState(() => {
    const savedCount = localStorage.getItem("count");
    return savedCount !== null ? parseInt(savedCount, 10) : 0;
  });

  const [countTrue, setCountTrue] = useState(100);

  useEffect(() => {
    localStorage.setItem("count", count.toString());

    while (countTrue < 100) {
      const interval = setInterval(() => {
        if (countTrue < 100) {
          setCountTrue(countTrue + 1);
        } else {
          clearInterval(interval);
        }
      }, 1500); // Прибавляем по 1 каждые 1.5 секунды
    }
  }, [count, countTrue]);

  const handleClick = () => {
    setCount(count + 1);
    setCountTrue(countTrue - 1);
  };

  return (
    <div className="App">
      <h2 className="title">Quack Coin</h2>
      <p className="count-click">{count}</p>
      <div className="coin-btn">
        <button className="coin-button" onClick={handleClick}>
          ...
        </button>
      </div>
      <div className="coin-progress" style={{ width: countTrue }}>
        {countTrue}
      </div>
    </div>
  );
}

export default App;
